item.description:

        {{- $page := . -}}
        {{- with .Params.image -}}
            {{- $url := . -}}

            {{- if findRE "^(/|http(s?)://)" $url -}}
                {{- $url = absURL $url -}}
            {{- else -}}
                {{- $url = ($page.Resources.GetMatch $url).Permalink -}}
            {{- end -}}

            {{- printf `

` $url -}}
        {{- end -}}

        {{- $summary := .Params.description | markdownify | default .Summary | replaceRE "(?s)" "" | replaceRE "(?s)
" "" }}
        {{- if in $summary "
" }}{{ $summary }}{{ else }}{{ printf "
%s" $summary }}{{ end }}

        {{- printf `
%s` .Permalink (T "readOn") -}}
